INSERT INTO form_config (type_id, config_key, config_value, update_time) VALUES ( (SELECT id FROM ad_type WHERE type_code = 'short_video' LIMIT 1), 'ad_create_form', '{ "formTitle": "创建短视频广告", "fields": [ { "name": "title", "type": "input", "label": "广告标题", "placeholder": "请输入吸引用户的短视频标题（不超过30字）", "rules": { "required": true, "maxLength": 30, "message": { "required": "标题不能为空", "maxLength": "标题不能超过30字" } }, "style": { "width": "100%" } }, { "name": "videos", "type": "video-upload", "label": "短视频素材", "desc": "支持上传1-2个短视频，仅支持MP4格式，单文件≤100MB，时长5-60秒，分辨率9:16（竖屏）", "rules": { "required": true, "maxCount": 2, "fileSize": 104857600, "fileType": ["video/mp4"], "duration": { "min": 5, "max": 60 }, "resolution": "9:16", "message": { "required": "请上传短视频素材", "maxCount": "最多上传2个短视频", "fileSize": "单视频大小不能超过100MB", "fileType": "仅支持MP4格式视频", "duration": "视频时长需在5-60秒之间", "resolution": "请上传9:16竖屏短视频" } }, "uploadUrl": "/api/videos/upload", "preview": true }, { "name": "cover_frame", "type": "video-cover", "label": "视频封面", "desc": "从第一个视频中截取封面，或自定义上传图片", "depends": { "field": "videos", "condition": "notEmpty" }, "rules": { "required": true }, "options": { "fromVideo": true, "customUpload": true } }, { "name": "price", "type": "number", "label": "初始出价（元/千次曝光）", "placeholder": "请输入出价，最低0.5元", "rules": { "required": true, "min": 0.5, "step": 0.1, "message": { "required": "出价不能为空", "min": "出价最低0.5元", "step": "出价精度为0.1元" } }, "style": { "width": "300px" }, "defaultValue": 1.0 }, { "name": "launch_time", "type": "datetime-range", "label": "投放时段", "rules": { "required": true }, "placeholder": ["开始时间", "结束时间"], "minDate": "today" } ], "submitText": "创建短视频广告", "resetText": "重新填写" }', CURRENT_TIMESTAMP );
INSERT INTO form_config (type_id, config_key, config_value, update_time) VALUES ( (SELECT id FROM ad_type WHERE type_code = 'brand' LIMIT 1), 'ad_create_form', '{ "formTitle": "创建品牌广告", "fields": [ { "name": "title", "type": "input", "label": "广告标题", "placeholder": "请输入品牌广告标题（不超过20字）", "rules": { "required": true, "maxLength": 20 } }, { "name": "brand_logo", "type": "image-upload", "label": "品牌LOGO", "desc": "支持PNG/JPG格式，大小≤5MB，背景透明优先", "rules": { "required": true, "fileSize": 5242880, "fileType": ["image/png", "image/jpeg"] }, "uploadUrl": "/api/images/upload", "preview": true }, { "name": "videos", "type": "video-upload", "label": "品牌宣传视频", "desc": "支持上传1个视频，MP4/AVI格式，单文件≤200MB，时长15-120秒", "rules": { "required": true, "maxCount": 1, "fileSize": 209715200, "fileType": ["video/mp4", "video/avi"], "duration": { "min": 15, "max": 120 } }, "uploadUrl": "/api/videos/upload", "preview": true }, { "name": "enterprise_qualification", "type": "file-upload", "label": "企业资质", "desc": "请上传营业执照扫描件（PDF格式，大小≤10MB）", "rules": { "required": true, "fileSize": 10485760, "fileType": ["application/pdf"] }, "uploadUrl": "/api/files/upload" }, { "name": "price", "type": "number", "label": "初始出价（元/千次曝光）", "rules": { "required": true, "min": 5.0, "step": 0.5 }, "defaultValue": 10.0, "desc": "品牌广告基础出价较高，建议出价≥10元以保障曝光" }, { "name": "contact_info", "type": "group", "label": "品牌联系人信息", "fields": [ { "name": "contact_name", "type": "input", "label": "联系人姓名", "rules": { "required": true } }, { "name": "contact_phone", "type": "input", "label": "联系电话", "rules": { "required": true, "pattern": "^1[3-9]\\d{9}$" } } ] } ], "submitText": "提交品牌广告审核", "resetText": "取消" }', CURRENT_TIMESTAMP );
INSERT INTO form_config (type_id, config_key, config_value, update_time) VALUES ( (SELECT id FROM ad_type WHERE type_code = 'effect' LIMIT 1), 'ad_create_form', '{ "formTitle": "创建效果广告", "fields": [ { "name": "title", "type": "input", "label": "广告标题", "rules": { "required": true, "maxLength": 25 } }, { "name": "conversion_target", "type": "select", "label": "转化目标", "options": [ { "label": "App下载", "value": "app_download" }, { "label": "表单提交", "value": "form_submit" }, { "label": "商品购买", "value": "goods_buy" } ], "rules": { "required": true }, "changeCallback": "loadTargetFields" }, { "name": "target_fields", "type": "dynamic-group", "label": "转化目标配置", "depends": { "field": "conversion_target", "condition": "notEmpty" }, "fieldsMap": { "app_download": [ { "name": "app_url", "type": "input", "label": "App下载链接", "rules": { "required": true, "pattern": "^https?://" } }, { "name": "app_size", "type": "input", "label": "App大小（MB）", "rules": { "required": true, "type": "number", "min": 0.1 } } ], "form_submit": [ { "name": "form_title", "type": "input", "label": "表单标题", "rules": { "required": true } }, { "name": "form_fields", "type": "checkbox-group", "label": "表单字段", "options": [ { "label": "姓名", "value": "name" }, { "label": "电话", "value": "phone" }, { "label": "邮箱", "value": "email" } ], "rules": { "required": true, "minCount": 2 } } ], "goods_buy": [ { "name": "goods_id", "type": "input", "label": "商品ID", "rules": { "required": true } }, { "name": "goods_price", "type": "number", "label": "商品单价", "rules": { "required": true, "min": 0.01 } } ] } }, { "name": "videos", "type": "video-upload", "label": "效果宣传视频", "desc": "支持上传1个视频，MP4格式，单文件≤150MB，时长10-90秒", "rules": { "required": true, "maxCount": 1, "fileSize": 157286400, "fileType": ["video/mp4"], "duration": { "min": 10, "max": 90 } }, "uploadUrl": "/api/videos/upload", "preview": true }, { "name": "orientation", "type": "group", "label": "定向配置", "fields": [ { "name": "age_range", "type": "select", "label": "目标年龄", "options": [ { "label": "18-24岁", "value": "18-24" }, { "label": "25-30岁", "value": "25-30" }, { "label": "31-40岁", "value": "31-40" }, { "label": "40岁以上", "value": "40+" } ], "multiple": true, "rules": { "required": true } }, { "name": "region", "type": "city-picker", "label": "投放地域", "level": "city", "multiple": true, "rules": { "required": true } } ] }, { "name": "price", "type": "number", "label": "初始出价（元/转化）", "rules": { "required": true, "min": 0.1, "step": 0.01 }, "defaultValue": 1.0 } ], "submitText": "创建效果广告", "resetText": "重置" }', CURRENT_TIMESTAMP );